<style scoped lang="scss">
@import '../../../styles/variables.scss';

.block {
  width: 100%;
  height: 250px;
  background-color: $white-color;
  border-radius: $block-border-radius;
}

.category {
  color: $placeholder;
  margin-right: 10px;
  font-size: 12px;
  cursor: pointer;
  &:hover {
    color: $black-color;
  };
}

.active {
  color: $black-color;
  font-weight: bolder;
  border-bottom: 4px solid $blue-normal;
  z-index: 2;
}

.border {
  position: absolute;
  bottom: 1px;
}

</style>
