<style scoped lang="scss">
@import '../../../styles/variables.scss';

.card {
  width: 100%;
  height: 200px;
  margin: 10px 0;
  background-color: $white-color;
  border-radius: $block-border-radius;
}

.category {
  color: $placeholder;
  font-size: 12px;
  margin-right: 10px;
  cursor: pointer;
  &:hover {
    color: $black-color;
  };
}

.active {
  color: $black-color;
  font-weight: bolder;
  border-bottom: 4px solid $blue-normal;
  z-index: 2;
}

.border {
  position: absolute;
  bottom: 1px;
}
.title {
  font-size: 12px;
}
.sku {
  font-size: 10px;
}

.view-all {
  font-size: 0.7rem;
  color: #0500FF;
  font-weight: bold;
  cursor: pointer;
}
</style>
