import {computed, PropType, ref} from "vue";
import {ProductItem} from '../../../app/types'
import {useProductStore} from "../../../store";

const props = defineProps({
  product: Object as PropType<ProductItem>
})

const productStore = useProductStore()

const status = ref(0)

const starColor = computed(() => status.value ? '#F8EC06FF' : 'none')
const strokeColor = computed(() => status.value ? '#F8EC06FF' : 'black')

const clickToStarHandler = () => {
  if(!props.product) {
    return
  }
